import React from 'react';

function DisplayArt(){
    return (
        <div className="bg-yellow-500 py-4">
            <div className="flex jusitfy-start">
                <div className="bg-yellow-700 text-right text-white leading-loose mb-3 px-3 py-2 w-11/12">
                    <h1 className="font-serif text-3xl mb-2">Want Exposure?</h1>
                    <p className="text-lg font-sans">Display and Sell Your Art in the Coffee Shop!</p>
                </div>
            </div>
            <div className="transform -translate-x-1/3 overflow-hidden">
                <img src="https://images.unsplash.com/photo-1523698120758-030a38a90d16?ixid=MnwxMjA3fDB8MHxzZWFyY2h8OXx8YXJ0JTIwZGlzcGxheXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=60" alt="" />
            </div>
            <div className="flex justify-end">
                <div className="w-1/2 bg-yellow-700 font-serif p-2 mt-2 text-xl text-white">
                    <h1 className="font-semibold">Start Here:</h1>
                    <p>8282896735</p>
                </div>
            </div>
        </div>
    );
}

export default DisplayArt;
