import React, {useState} from 'react';

function AddMenuItem(){

    const [menuItem, setMenuItem] = useState([{
        chosenPhoto: "",
        title: "",
        smallItemPrice: "",
        mediumItemPrice: "",
        largeItemPrice: ""
    }])

    function handleChange(event){
        const {name, value} = event.target;

        setMenuItem(prevItems => {
            return {
                ...prevItems,
                [name]: value
            }
        })
        console.log("Input value has changed.")
    }

    return (<div>
        <h1>Add Menu Item</h1>
        <form>
            <input 
                value={menuItem.photo}
                name="chosenPhoto"
                type="file" 
                accept="image/png, image/jpeg, image/svg+xml"
            />
            <input value={menuItem.title} name="title" onChange={handleChange} type="text" placeholder="Title" />
            <input value={menuItem.smallItemPrice} name="small-item-price" onChange={handleChange} type="text" placeholder="Price For Small Size" />
            <input value={menuItem.mediumItemPrice} name="medium-item-price" onChange={handleChange} type="text" placeholder="Price For Medium Size" />
            <input value={menuItem.largeItemPrice} name="large-item-price" onChange={handleChange} type="text" placeholder="Price For Large Size" />
            <button type="submit"></button>
        </form>
    </div>);
}

export default AddMenuItem;
