function Roaster(){

    const originalStyle = "my-5 w-full h-auto mx-auto transform -translate-x-5 -translate-y-7";
    const newStyle = "my-5 w-full h-auto mx-auto transform -translate-x-1 -translate-y-1";

    const [roasterPicStyle, setRoasterPicStyle] = useState()

    return (
        <div className="bg-yellow-700 pb-4">
            <h1 className="capitalize font-semibold font-serif py-4 px-4 text-3xl text-center text-white">Beans are prepared <span className="font-bold text-yellow-300">in house</span> for amazing results</h1>
            <div className="bg-yellow-300 w-3/4 h-auto mx-auto">
                <img className="my-5 w-full h-auto mx-auto transform -translate-x-5 -translate-y-7" src="https://images.unsplash.com/photo-1511537190424-bbbab87ac5eb?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1470&q=80" alt="coffee roaster" />
            </div>
            <div className="">
                <h1 className="text-left text-3xl text-white">This is where the magic happens!</h1>
            </div>
        </div>
    );
}

export default Roaster;
