import React, {useState, useEffect} from 'react';
import CoffeeHangOut from "../images/hang-out.svg";
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';
import {Link} from "react-router-dom";
import ProductList from "./ProductList";
import {Link as ScrollLink} from 'react-scroll';
import AOS from 'aos';


function Feature(){

    useEffect(() => {
        AOS.init({
            duration: 1000,
            startEvent: 'load'
        });
    }, [])

    const normalClassName = "bg-yellow-800 flex-grow rounded-3xl shadow-2xl text-white text-2xl md:transform md:translate-x-0 my-5 p-2";
    const alternateClassName = "bg-yellow-600 flex-grow rounded-3xl shadow-lg text-white text-2xl md:transform md:translate-x-0 my-5 p-2";

    const [arrowStyle, setArrowStyle] = useState(null);
    const [buttonStyle, setButtonStyle] = useState(normalClassName);

    function handleMouseOver(event){
        setArrowStyle("bg-yellow-600 transform translate-y-0.5");
        setButtonStyle(alternateClassName);
    }

    function handleMouseOut(event){
        setArrowStyle("bg-yellow-800 transform translate-y-0");
        setButtonStyle(normalClassName);
    }


    return (
        <div className="bg-yellow-100 h-screen">
            <div className="flex flex-row flex-wrap pt-20" data-aos="zoom-in">
                    
                        <h1 className="md:text-left text-6xl leading-tight">Freshly <span className="text-yellow-800" whileHover="text-white">Roasted. </span>Downtown <span className="text-yellow-400">Feel.</span></h1>
                        <img className="w-full h-auto mt-2 md:w-1/2 md:p-3 order-2" src={CoffeeHangOut} alt="coffee-hang-out" />
                        <div className="md:flex md:justify-start order-3">
                            <ScrollLink to="services" smooth={true} duration={2000}>
                                <button 
                                    onMouseOut={handleMouseOut} 
                                    onMouseOver={handleMouseOver} 
                                    className={buttonStyle}>
                                        Explore Fausto!
                                        <p 
                                            onMouseOut={handleMouseOut} 
                                            onMouseOver={handleMouseOver} 
                                            className={arrowStyle} 
                                        >
                                            <KeyboardArrowDownIcon fontSize="large" />
                                        </p> 
                                </button>
                            </ScrollLink>
                       
                    </div>
                    
                    {/* <div className="md:flex md:justify-start md:w-1/3 md:flex-col">
                       
                    </div> */}
            </div>
        </div>
    )
}

export default Feature;
