import React, {useState} from 'react';
import Header from './Header.jsx';
import Axios from 'axios';

function AdminFeature(){
    const url = "http://localhost:5000/features";
    const [data, setData] = useState({
        userPhoto: "",
        userTitle: ""
    })

    const headers = {
        "Access-Control-Allow-Origin" : '*',
    }

    (async () => {
        const rawResponse = await fetch('https://httpbin.org/post', {
          method: 'POST',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            userPhoto: data.userPhoto,
            userTitle: data.userTitle
            })
        });
        const content = await rawResponse.json();
      
        console.log(content);
      })();

    function submit(e){
        e.preventDefault();
        Axios.post(url,  
        {
            userPhoto: data.userPhoto,
            userTitle: data.userTitle
        },
        {headers}
        ).then(function(res){
            console.log(res.data)
        })

        
    }


    function handle(e){
        const newData = {...data};
        newData[e.target.id] = e.target.value;
        setData(newData);
        console.log(newData);
    }

    return (
        <div>
            <Header />
            <section class="admin-feature-section">
            <h1 class="section-title">Administrator Feature Assignment</h1>
            <form class="form-signin" onSubmit={(e) => submit(e)}>
                <div class="form-group row">
                    <label class="feature-label col-sm-2 col-form-label-lg">Photo:</label>
                    <input 
                    class="form-control col-sm-10" 
                    id="userPhoto" 
                    name="userPhoto"
                    type="text" 
                    onChange={function(e){
                        handle(e)
                    }} 
                    placeholder="Paste photo url here"></input>
                </div>
                <div class="form-group row">
                    <label class="feature-label col-sm-2 col-form-label-lg">Feature Title: </label>
                    <input class="form-control col-sm-10" id="userTitle" name="userTitle" onChange={function(e){
                        handle(e)
                    }} 
                    type="text" 
                    placeholder="Type the title of the feature here" />
                </div>
                
                <button class="btn btn-lg submit-btn" type="submit">Submit</button>
            </form>
            </section>
        </div>
    );
}

export default AdminFeature;
