import React from 'react';

function Item(){
    return(
        <div className="w-3/4 h-auto bg-yellow-500 mx-auto p-4 rounded-3xl">
                <img className="w-60 h-auto mx-auto mb-3 transform rotate-12" src={CoffeeToGo} alt="Coffee Cup" />
                <div className="bg-yellow-600 inline-block p-2 rounded-xl">
                    <h1 className="text-white text-4xl font-semibold py-2">Coffee</h1>
                </div>
                <div className="text-white text-right text-2xl mt-2 font-medium">
                    <p>Small: $0.99</p>
                    <p>Medium: $1.25</p>
                    <p>Large: $2.00</p>
                </div>
        </div>
    );
}

export default Item;
