import React, {useState} from 'react';
import CoffeeHangOut from "../images/hang-out.svg";
import KeyboardArrowDownIcon from '@mui/icons-material/KeyboardArrowDown';

function Practice(){

    const [isHoveredOver, setHoveredOver] = useState(false);
    const [isMousedOut, setMousedOut] = useState(false);

    function handleHover(event){
        setHoveredOver(true);
    }

    function handleMouseOut(event){
        setMousedOut(true)
    }


    return (
        <div className="container flex flex-row flex-wrap my-3">
            <h1 className="text-center md:text-left text-6xl leading-tight">Freshly Roasted. Downtown Feel</h1>
            <img className="w-full h-auto mt-2 " src={CoffeeHangOut} alt="coffee-hang-out" />
            <button onMouseOut="transform translate-y-0" onMouseOver={handleHover} className="bg-yellow-900 flex-grow rounded-3xl text-white text-2xl my-4 p-3">Explore Fausto<p onMouseOut={handleMouseOut} onMouseOver={handleHover} className={isMousedOut && "transform translate-y-0"} className={isHoveredOver ? "transform translate-y-2" : "transform translate-y-0"} ><KeyboardArrowDownIcon fontSize="large" /></p></button>
        </div>
    )
}

export default Practice;
