import React from 'react';

function DisplayArt(){
    return (
        <div className="bg-yellow-500 pb-2">
            <div className="text-right leading-loose mb-3 px-4 pt-4">
                <h1 className="font-serif text-3xl mb-2">Want Exposure?</h1>
                <p className="text-lg">Display and Sell Your Art in the Coffee Shop!</p>
            </div>
            <div className="pb-4 transform -translate-x-1/3 overflow-hidden">
                <img src="https://images.unsplash.com/photo-1523698120758-030a38a90d16?ixid=MnwxMjA3fDB8MHxzZWFyY2h8OXx8YXJ0JTIwZGlzcGxheXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=60" alt="" />
            </div>
            <div className="bg-yellow-700">
                <h1>Start Here</h1>
            </div>
        </div>
    );
}

export default DisplayArt;
