import React, {useEffect} from 'react';
import Event from '../images/event.svg';
import HotBeverage from '../images/hot_beverage.svg';
import AOS from 'aos';
import 'aos/dist/aos.css';

function Services(){

    useEffect(()=>{
        AOS.init({
            duration: 1500,
            startEvent: 'load'
        });
    }, []);
    

    const headingStyle="font-serif text-3xl md:text-5xl mb-2";
    const subheadingStyle="text-lg md:text-2xl font-sans";
    const imgStyle="overflow-hidden shadow-xl";
    const midStyle="md:flex-none";
    const gridTextStyle="bg-yellow-700 p-3 my-4 w-11/12 md:w-full md:grid md:place-items-center text-right text-white"
    const headingTextStyle="text-3xl md:text-7xl md:text-center"
    const paragraphTextStyle="text-xl md:text-2xl md:text-center"

    return (
        <div className="bg-yellow-500">
            <div className="grid grid-cols-1 md:grid-cols-2 pt-5" id="services">
                <div className={gridTextStyle} data-aos="fade-in">
                    <h1 className={headingTextStyle}>Are You A Local Artist?</h1>
                    <p className={paragraphTextStyle}>Display and Sell Your Art in the Coffee Shop!</p>
                </div>
                <div className="justify-self-end w-11/12">
                    <img src="https://images.unsplash.com/photo-1523698120758-030a38a90d16?ixid=MnwxMjA3fDB8MHxzZWFyY2h8OXx8YXJ0JTIwZGlzcGxheXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=600&q=60" alt="" data-aos="zoom-in" />
                </div>
                <div className={gridTextStyle}>
                    <div data-aos="fade-in" className="">
                        <h1 className="">Need a Place to Gather?</h1>
                        <p className="">Look No Further. Our Space is Avaible to Rent for any Type of Event!</p>
                    </div>
                </div>
                <div className="">
                    <div className="">
                        <img src={Event} alt="event happening" data-aos="zoom-in" />
                    </div>
                </div>
                <div className={gridTextStyle} >
                    <div className="" data-aos="fade-in">
                        <h1 className="">Or We Can Come To You!</h1>
                        <p className="">Fausto Offers Top of the Line Catering to Spice Up Your Event!</p>
                    </div>
                </div>
                <div className="">
                    <div className="">
                        <img src={HotBeverage} alt="hot beverage" data-aos="zoom-in" />
                    </div>
                </div>
                <div className="">
                    <div data-aos="fade-right" className="">
                        <h1 className="font-semibold">Start Here:</h1>
                        <p>(828)289-6735</p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Services;
